mapper.select:
SELECT * FROM pack

     AND id = #{id}, 
     AND sender_work_node_id = #{senderWorkNodeId} 
     AND receiver_work_node_id = #{receiverWorkNodeId} 
     AND status = #{status} 
     AND current_work_node_id = #{currentWorkNodeId} 
     AND (sender_name = #{keyword} OR receiver_name = #{keyword} OR sender_phone = #{keyword} OR receiver_phone = #{keyword} ) 

    ORDER BY id DESC LIMIT #{start}, #{pageSize}

mapper.update:
update pack
sender_work_node_id = #{senderWorkNodeId}, receiver_work_node_id = #{receiverWorkNodeId}, status = #{status}, current_work_node_id = #{currentWorkNodeId}, frozen_time = #{frozenTime}
where id = #{id}

mapper.select:
SELECT count(*) FROM pack

     AND id = #{id}, 
     AND sender_work_node_id = #{senderWorkNodeId} 
     AND receiver_work_node_id = #{receiverWorkNodeId} 
     AND status = #{status} 
     AND current_work_node_id = #{currentWorkNodeId} 
     AND (sender_name = #{keyword} OR receiver_name = #{keyword} OR sender_phone = #{keyword} OR receiver_phone = #{keyword} )
