mapper.select:
SELECT * FROM pack
AND id = #{id}, AND sender_work_node_id = #{senderWorkNodeId}, AND receiver_work_node_id = #{receiverWorkNodeId}, AND status = #{status}, AND create_time = #{createTime}, AND current_work_node_id = #{currentWorkNodeId},
ORDER BY id DESC LIMIT #{start}, #{pageSize}

mapper.update:
update pack
sender_work_node_id = #{senderWorkNodeId}, receiver_work_node_id = #{receiverWorkNodeId}, status = #{status}, current_work_node_id = #{currentWorkNodeId},
where id = #{id}
